function resizePages(){var e=$(window).height();$(".page").css("min-height",e-5);$(".page > div").each(function(){var t=$(this).height();t+120<e&&$(this).css("margin-top",e/2-t/2-100)})}$(function(){$('.nav li a[href^="#"]').click(function(e){e.preventDefault();$("html,body").animate({scrollTop:$(this.hash).offset().top},200);return!1})});$(document).ready(function(){resizePages();$(".fancybox").fancybox();$("#submit").click(function(){var e=$("#name").val(),t=$("#email").val(),n=$("#message").val();$.ajax({type:"POST",url:"email.php",data:"name="+e+"&email="+t+"&message="+n,success:function(e){alert(e);e==="success"?$("#message").html("<div class='alert alert-success'>Mail sent!</div>"):$("#message").html("<div class='alert alert-error'>An error occured while sending the mail.</div>")}})});$(window).resize(function(){resizePages();$('[data-spy="scroll"]').each(function(){$(this).scrollspy("refresh")})})});