function resizePages(){var e=$(window).height();$(".page").css("min-height",e-5);$(".page > div").each(function(){var t=$(this).height();t+120<e&&$(this).css("margin-top",e/2-t/2-100)})}function sendEmail(){var e=$("#name").val(),t=$("#email").val(),n=$("#subject").val(),r=$("#message").val();if(e===""||t===""||n===""||r===""){$(".alert-message").html("Please fill all input-fields.");return}$.ajax({type:"POST",url:"email.php",data:"name="+e+"&email="+t+"&subject="+n+"&message="+r,success:function(e){if(e==="success"){$(".alert-message").html("Mail sent!");$("#name").reset();$("#email").reset();$("#subject").reset();$("#message").reset()}else $(".alert-message").html("An error occured while sending the email.")}})}$(function(){$('.nav li a[href^="#"]').click(function(e){e.preventDefault();$("html,body").animate({scrollTop:$(this.hash).offset().top},200);return!1})});$(document).ready(function(){resizePages();$(".fancybox").fancybox();$("#submit").click(function(){sendEmail()});$(window).resize(function(){resizePages();$('[data-spy="scroll"]').each(function(){$(this).scrollspy("refresh")})})});